use tokio::{
    io::{AsyncBufReadExt, AsyncWriteExt, BufReader},
    net::TcpListener,
    sync::broadcast,
};

#[tokio::main]
async fn main() {
    let listener = TcpListener::bind("localhost:5555").await.unwrap();

    println!("listening at localhost:5555");

    let (_tx, _rx) = broadcast::channel(10);

    loop {
        let (mut socket, addr) = listener.accept().await.unwrap();
        println!("hello! {addr} is online");
        let tx = _tx.clone();
        let mut rx = _tx.subscribe();

        _tx.send((format!("{} enter chat\n", addr), addr)).unwrap();

        tokio::spawn(async move {
            let (reader, mut writer) = socket.split();
            let mut reader = BufReader::new(reader);
            let mut line = String::new();

            loop {
                tokio::select! {
                    bytes_read = reader.read_line(&mut line) => {
                        if bytes_read.unwrap() == 0 {
                            continue;
                        }
                        tx.send((line.clone(), addr)).unwrap();
                    }
                    msg = rx.recv() => {
                        let (msg_info, msg_addr) = msg.unwrap();
                        if msg_addr != addr {
                            writer.write_all(msg_info.as_bytes()).await.unwrap();
                        }
                    }
                }
            }
        });
    }
}

// 这种场景在if语句中使用 := 声明了和最外层client同名的变量，这样会出现内部变量覆盖外层变量的
// 情况，编写这样错误代码的原因是没有了解go中声明新变量的用法
var client *http.Client
if tracing {
	client, err := createClientWithTracing()
	if err != nil {
		return err
	}
	log.Println(client)
} else {
	client, err := createDefaultClient()
	if err != nil {
		return err
	}
	log.Println(client)
}

// 把判断语句中的声明语法换成赋值语法，同样在最外层声明err，可以做到减少代码量的效果
var client *http.Client
var err error
if tracing {
	client, err = createClientWithTracing()
} else {
	client, err = createDefaultClient()
}
if err != nil {
		return err
}
log.Println(client)

func main() {
  // 在defer中执行 recover 可以恢复panic，defer也会在周围出现panic的时候执行
	defer func() {
		if r := recover(); r != nil {
			fmt.Println("recover", r)
		}
	}()
	f()
}
func f() {
	fmt.Println("a")
	panic("foo")
	fmt.Println("b")
}
