title:
// 这种场景在if语句中使用 := 声明了和最外层client同名的变量，
// 这样会出现内部变量覆盖外层变量的情况
var client *http.Client
if tracing {
	// 这里的client是新的临时变量，实际上没有对外层的client赋值
	client, err := createClientWithTracing()
	if err != nil {
		return err
	}
	log.Println(client)
} else {
	client, err := createDefaultClient()
	if err != nil {
		return err
	}
	log.Println(client)
}

// 把判断语句中的声明语法换成赋值语法，同样在最外层声明err，可以做到减少代码量的效果
var client *http.Client
var err error
if tracing {
	client, err = createClientWithTracing()
} else {
	client, err = createDefaultClient()
}
if err != nil {
		return err
}
log.Println(client)

func main() {
  // 在defer中执行 recover 可以恢复panic，defer也会在周围出现panic的时候执行
	defer func() {
		if r := recover(); r != nil {
			fmt.Println("recover", r)
		}
	}()
	f()
}
func f() {
	fmt.Println("a")
	panic("foo")
	fmt.Println("b")
}
