func convertGivenCapacity(foos []Foo) []Bar {
	n := len(foos)
  // 预先指定 cap
	bars := make([]Bar, 0, n)

	for _, foo := range foos {
		bars = append(bars, fooToBar(foo))
	}
	return bars
}

func convertGivenLength(foos []Foo) []Bar {
	n := len(foos)
	// 预先指定len，通过下标赋值，比使用append内置函数效率更高一些
	bars := make([]Bar, n)

	for i, foo := range foos {
		bars[i] = fooToBar(foo)
	}
	return bars
}

